package com.java;

public class BitwiseOpertaor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int number1 = 8;
		    int number2 = 4;
		    
		   
		    System.out.println(number1 >> 2);   
		    System.out.println(number2 >> 2);
	
		    System.out.println(number1 << 2);   
		    System.out.println(number2 << 2);
		    
		    

	}

}
