package com.java;

public class MaxNo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=2,b=5,c=9;
		
		if(a>b&&a>c) {
			System.out.println( a+" a is max");
			
		} 
		else if(b>a&&b>c){
			System.out.println( b+ "b is max");
			
		}
		else {
			System.out.println( c+ "c is max");
		}

	}

}
